export default function Specials() {
  const specials = [
    {
      title: "Greek Salad",
      description: "A refreshing salad with feta cheese, tomatoes, and olives.",
      image: "images/greek-salad.jpg",
    },
    {
      title: "Bruschetta",
      description: "Grilled bread with garlic, tomatoes, and basil.",
      image: "images/bruschetta.jpg",
    },
    {
      title: "Lemon Dessert",
      description: "A sweet and tangy lemon dessert to end your meal.",
      image: "images/lemon-dessert.jpg",
    },
  ];

  return (
    <section className="py-12 px-4 text-center">
      <h2 className="text-3xl font-bold mb-8 text-gray-800">This Week's Specials</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {specials.map((item, index) => (
          <div key={index} className="bg-white shadow-lg rounded-lg overflow-hidden">
            <img src={item.image} alt={item.title} className="w-full h-48 object-cover" />
            <div className="p-6">
              <h3 className="text-xl font-semibold mb-2 text-green-700">{item.title}</h3>
              <p className="text-gray-600 text-sm">{item.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
