import React from "react";

function Testimonials() {
  return (
    <section id="testimonials" style={{ padding: "2rem", backgroundColor: "#e0e7ff" }}>
      <h2>What Our Customers Say</h2>
      <blockquote>"Amazing service and delicious food!" - Sara</blockquote>
      <blockquote>"A hidden gem in the city. Highly recommend!" - Ahmed</blockquote>
    </section>
  );
}

export default Testimonials;
