export default function Hero() {
  return (
    <section className="bg-green-100 text-center py-16 px-4">
      <h1 className="text-4xl font-bold mb-4 text-green-800">
        Welcome to Little Lemon
      </h1>
      <p className="text-lg text-gray-700 mb-6 max-w-xl mx-auto">
        We serve delicious Mediterranean food made with love.
      </p>
      <button className="bg-yellow-500 hover:bg-yellow-600 text-white font-semibold py-2 px-6 rounded shadow-md transition">
        Reserve a Table
      </button>
    </section>
  );
}
